import React from 'react';

import Title from './Title';
import Todos from './Todos';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      temp: '',
      handling: ['Buy groceries', 'run for an hour', 'do squats',]
    };
  }

  addTodo = (e) => {
    e.preventDefault();
    var joinedState = this.state.handling.concat(this.state.temp);
    this.setState({
      handling: joinedState,
    });
  };

  handleChange = (e) => {
    this.setState({
      temp: e.target.value
    });
  };

  deleteTodo = (index) => {
    let todoList = [...this.state.handling];
    todoList.splice(index,1);
    this.setState({handling:todoList});
  };

  editTodo = (index, e) => {
    let todoList = [...this.state.handling];
    let newInput = e.target.value;
    todoList.splice(index,1, newInput);
    this.setState({handling: todoList});
  };

  render(){
    return (
      <div className="ui container segment">
        <Title onSubmit={this.addTodo} onUpdateInput={this.handleChange}/>
        <Todos handling={this.state.handling} deleteTodo={this.deleteTodo} editTodo={this.editTodo}/>
      </div>
    );
  };

}
export default App;
