import Title from './Title';
import Todos from './Todos';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      handling: ['Buy groceries',]
    };
  }

  AddTodo = (e) => {
    e.preventDefault();
    //var joinedState = this.state.handling.concat(.target.value);
    console.log(this.state);
    this.setState({
      //handling: joinedState,
    });
  };

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
    console.log(this.state);
  };

  render(){
    return (
      <div className="ui container segment">
        <Title onSubmit={this.AddTodo} onUpdateInput={this.handleChange}/>
        <Todos handling={this.state.handling}/>
      </div>
    );
  };

}
export default App;
