import React from 'react';

import Title from './Title';
import Todos from './Todos';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      temp: '',
      handling: ['Buy groceries', 'run for an hour',]
    };
  }

  addTodo = (e) => {
    e.preventDefault();
    var joinedState = this.state.handling.concat(this.state.temp);
    console.log(e.target.value);
    this.setState({
      handling: joinedState,
    });
  };

  handleChange = (e) => {
    this.setState({
      temp: e.target.value
    });
  };

  deleteTodo = (itemId) => {
    console.log(itemId);
    const items = this.state.items.filter(item => item.id !== itemId);
    this.setState({ handling: items });
    console.log('delete called');
  };

  render(){
    return (
      <div className="ui container segment">
        <Title onSubmit={this.addTodo} onUpdateInput={this.handleChange}/>
        <Todos handling={this.state.handling} deleteTodo={this.deleteTodo()}/>
      </div>
    );
  };

}
export default App;
