import React from 'react';

import Title from './Title';
import Todos from './Todos';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      temp: '',
      standardStyle: 'ui clearing segment',
      handling: [
        {
          name:'Buy groceries',
          style: 'ui clearing segment',
          disabled: ''
        },
        {
          name:'Run a mile',
          style: 'ui clearing segment',
          disabled: 'disabled'
        }
      ],
      todoStyle: '',
      finished: false,
    };
  }

  addTodo = (e) => {
    e.preventDefault();
    const joinedState = [
      ...this.state.handling,
        {
          name:this.state.temp,
          style:this.state.standardStyle
        }
    ];
    this.setState({
      handling: joinedState,
    });
  };

  handleChange = (e) => {
    this.setState({
      temp: e.target.value
    });
  };

  deleteTodo = (index) => {
    let todoList = [...this.state.handling];
    todoList.splice(index,1);
    this.setState({handling:todoList});
  };

  editTodo = (index, e) => {

    //første forsøk på endre. Event target er undefined, siden det er knappen jeg trykker på.
    /*
    let todoList = [...this.state.handling];
    let newInput = e.target.value; // event is undefined
    console.log(newInput);
    todoList.splice(index,1, newInput);
    this.setState({handling: todoList});*/
  };

  finishTodo = (index, e) => {
    const objectArrayFull = this.state.handling;

    const todoList = this.state.handling[index];
    todoList.style = this.state.standardStyle + ' disabled';
    console.log(index)
    objectArrayFull[index] = todoList;
    console.log(objectArrayFull)

    this.setState({todoStyle: 'disabled'});
  };

  render(){
    return (
      <div className="ui container segment">
        <Title onSubmit={this.addTodo} onUpdateInput={this.handleChange}/>
        <Todos
          handling={this.state.handling}
          deleteTodo={this.deleteTodo}
          editTodo={this.editTodo}
          finishTodo={this.finishTodo}
          todoStyle={this.state.todoStyle}
        />
      </div>
    );
  };

}
export default App;
