import React from 'react';

import Title from './Title';
import Todos from './Todos';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      temp: '',
      handling: ['Buy groceries', 'run for an hour',]
    };
  }

  addTodo = (e) => {
    e.preventDefault();
    var joinedState = this.state.handling.concat(this.state.temp);
    this.setState({
      handling: joinedState,
    });
  };

  handleChange = (e) => {
    this.setState({
      temp: e.target.value
    });
  };

  deleteTodo = (e, index) => {
    let todoList = [...this.state.handling];
    console.log('index is : ' + index);
    todoList.splice(index,1);
    this.setState({handling:todoList});
  };

  render(){
    return (
      <div className="ui container segment">
        <Title onSubmit={this.addTodo} onUpdateInput={this.handleChange}/>
        <Todos handling={this.state.handling} deleteTodo={(e, index) => {this.deleteTodo(e, index)}}/>
      </div>
    );
  };

}
export default App;
