import React from 'react';

import Title from './Title';
import Todos from './Todos';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      temp: '',
      handling: ['Buy groceries',]
    };
  }

  AddTodo = (e) => {
    e.preventDefault();
    var joinedState = this.state.handling.concat(this.state.temp);
    console.log(e.target.value);
    this.setState({
      handling: joinedState,
    });
  };

  handleChange = (e) => {
    this.setState({
      temp: e.target.value
    });
  };

  render(){
    return (
      <div className="ui container segment">
        <Title onSubmit={this.AddTodo} onUpdateInput={this.handleChange}/>
        <Todos handling={this.state.handling}/>
      </div>
    );
  };

}
export default App;
