import React from 'react';

import Title from './Title';
import Todos from './Todos';
import ModalTiny from './Modal';

class App extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      temp: '',
      handling: ['Buy groceries', 'Run a 10k', 'Do a SL 5x5 workout',],
      disabled: '',
      open: false,
    };
  }

  addTodo = (e) => {
    e.preventDefault();
    var joinedState = this.state.handling.concat(this.state.temp);
    this.setState({
      handling: joinedState,
    });
  };

  handleChange = (e) => {
    this.setState({
      temp: e.target.value
    });
  };

  deleteTodo = (index) => {
    let todoList = [...this.state.handling];
    todoList.splice(index,1);
    this.setState({handling:todoList});
  };

  editTodo = (index, e) => {

    //første forsøk på endre. Event target er undefined, siden det er knappen jeg trykker på.
    /*
    let todoList = [...this.state.handling];
    let newInput = e.target.value; // event is undefined
    console.log(newInput);
    todoList.splice(index,1, newInput);
    this.setState({handling: todoList});*/
  };

  finishTodo = (index, e) => {
    console.log('state : ' + this.state.disabled);
    this.setState({disabled: 'disabled'});
  };

  render(){
    return (
      <div className="ui container segment">
        <ModalTiny />
        <Title onSubmit={this.addTodo} onUpdateInput={this.handleChange}/>
        <Todos
          handling={this.state.handling}
          deleteTodo={this.deleteTodo}
          editTodo={this.editTodo}
          finishTodo={this.finishTodo}
          todoStyle={this.state.disabled}
        />
      </div>
    );
  };

}
export default App;
